(152, '_transient_acf_get_remote_plugin_info', 'a:13:{s:4:\"name\";s:26:\"Advanced Custom Fields PRO\";s:4:\"slug\";s:26:\"advanced-custom-fields-pro\";s:8:\"homepage\";s:37:\"https://www.advancedcustomfields.com/\";s:7:\"version\";s:5:\"5.7.5\";s:6:\"author\";s:13:\"Elliot Condon\";s:10:\"author_url\";s:28:\"http://www.elliotcondon.com/\";s:12:\"contributors\";s:12:\"elliotcondon\";s:8:\"requires\";s:5:\"4.4.0\";s:6:\"tested\";s:5:\"4.9.9\";s:6:\"tagged\";s:61:\"acf, advanced, custom, field, fields, form, repeater, content\";s:9:\"changelog\";s:538:\"<h4>5.7.5</h4><p><em>Release Date - 6 September 2018</em></p>\n<ul>\n<li> Fix - Fixed bug causing multisite login redirect issues.</li>\n<li> Fix - Fixed bug causing validation issues in older versions of Firefox.</li>\n<li> Fix - Fixed bug causing duplicate Select2 instances when adding a widget via drag/drop.</li>\n<li> Dev - Improved WPML compatibility by using <code>$sitepress->get_current_language()</code> instead of <code>ICL_LANGUAGE_CODE</code>.</li>\n<li> Dev - Improved validation JS with new Validator model and logic.</li>\n</ul>\";s:14:\"upgrade_notice\";s:0:\"\";s:5:\"icons\";a:1:{s:7:\"default\";s:63:\"https://ps.w.org/advanced-custom-fields/assets/icon-256x256.png\";}}', 'no'),
(153, 'se_meta', 'a:10:{s:7:\"blog_id\";b:0;s:7:\"api_key\";b:0;s:8:\"auth_key\";b:0;s:7:\"version\";s:5:\"8.1.9\";s:13:\"first_version\";s:5:\"8.1.9\";s:8:\"new_user\";b:1;s:4:\"name\";s:0:\"\";s:5:\"email\";s:0:\"\";s:24:\"show_options_page_notice\";b:0;s:16:\"se_global_notice\";a:2:{s:5:\"title\";s:28:\"Searching for your car keys?\";s:7:\"message\";s:324:\"Well, there are some things our plugin can\'t search for - your car keys, your wallet, a soulmate and <strong>unregistered custom post types</strong> :) <br> It searches for almost everything else, but it also does some other amazing stuff, like ... research. <a href=\"http://zem.si/1l7q5KS\" target=\"_blank\">Check it out!</a>\";}}', 'yes'),
